use Symfony\Component\HttpFoundation\Response;
use Symfony\Component\Routing\Attribute\Route;
use Symfony\Bundle\FrameworkBundle\Controller\AbstractController;

class ConjugationController extends AbstractController
{

    private $verbeService;
    private EntityManagerInterface $entityManager;

    public function __construct(VerbeService $verbeService, EntityManagerInterface $entityManager)
    {
        $this->verbeService = $verbeService;
        $this->entityManager = $entityManager;
        
    }
    
    #[Route('/conjugation', name: 'app_conjugation')]
    public function index(Request $request): Response
    {
        $locale = $request->getLocale(); // Get the current locale
        $template = $locale === 'en' ? 'conjugation/conjugation_en.html.twig' : 'conjugation/conjugation_fr.html.twig';

 // Récupérer tous les verbes depuis la base de données
    $verbes = $this->entityManager->getRepository(Verbe::class)->findAll();
    $totalItems = count($verbes);
    $totalVerbes = $this->verbeService->countVerbes();

    return $this->render($template, [
        'verbes' => $verbes,
        'totalItems' => $totalItems,
    ]);
    }

    #[Route('/verbe', name: 'app_verbe')]
    public function conjug(Request $request): Response
    {
        $terme = $request->query->get('terme');
        $lang = $request->query->get('lang');
    
         if (!$terme || !$lang) {
        return $this->render('conjugation/verbes.html.twig', [
            'verbe' => null,
            'verbExists' => false,
            'lang' => $lang ?? 'fr-rif',
            'englishVerbs' => false,
        ]);
    }
        // Mappage des langues vers les champs de la base de données
        $mapping = [
            'rif-fr'  => 'verbeRifain',  // Corrigé pour correspondre aux noms de propriétés
            'fr-rif' => 'verbeFrancais',  // Corrigé pour correspondre aux noms de propriétés
            'en-rif' => null, 
            'rif-en' => null,  
        ];
    
        // Récupérer le verbe correspondant depuis la base de données
        $verbe = null;
        if ($lang !== 'en-rif' && $lang !== 'rif-en') {
            $verbe = $this->entityManager->getRepository(Verbe::class)->findOneBy([
                $mapping[$lang] => $terme
            ]);
        }
        
        // Vérifier si le verbe a été trouvé
        if (!$verbe) {
            return $this->render('conjugation/verbes.html.twig', [
                'verbe' => null,
                'verbExists' => false,
                'lang' => $lang,
                'englishVerbs' => false,
            ]);
        }
    
        // Préparer les conjugaisons
        $conjugations = $this->prepareConjugations($verbe);
    
        return $this->render('conjugation/verbes.html.twig', [
            'verbe' => $verbe,
            'verbExists' => true,
            'lang' => $lang,
            'conjugations' => $conjugations,
        ]);
    }

